INSERT INTO products VALUES
(31, "Pegamento Perfecto Placas", "https://perfecto.com.ar/images/producto-placas.jpg", 1600, "Mezcla adhesiva, de base cementícea, apta para aplicaciones “especiales”, de baja absorción de agua, para la colocación de todo tipo de revestimientos cerámicos y pétreos sobre placas de yeso.", 1),
(32, "Pegamento Perfecto Impermeable", "https://perfecto.com.ar/images/producto-impermeable.jpg", 1600, "Es una mezcla adhesiva cementícea para la colocación de revestimientos cerámicos, calcáreos, graníticos y pétreos, de mediana y alta absorción, en interiores y exteriores. Es un adhesivo de baja absorción de agua (I) y de fraguado rápido (F), elaborada a base de Cemento Pórtland de muy alta resistencia, arenas seleccionadas y aditivos de última generación.", 1),
(33, "Pegamento Perfecto Porcellanato", "https://perfecto.com.ar/images/producto-porcellanato.jpg", 1600, "Mezcla adhesiva cementícea de baja absorción de agua, fraguado rápido y resistente al congelamiento y deshielo, para la colocación de todo tipo de revestimientos (baja, media y alta absorción), en interiores y exteriores, de grandes dimensiones, como los porcellanatos y otros materiales pétreos. Elaborada a base de Cemento Pórtland de muy alta resistencia, arenas seleccionadas, aditivos de última generación y polímeros.", 1),
(34, "Pegamento Perfecto Flexible", "https://perfecto.com.ar/images/producto-flexible.jpg", 1600, "Mezcla adhesiva cementícea, de baja absorción de agua y resistente al congelamiento y deshielo, para la colocación de todo tipo de revestimientos cerámicos, porcellantos y materiales pétreos, tanto en interiores como en exteriores. Elaborada a base de cemento portland de muy alta resistencia, arenas seleccionadas, aditivos de última generación y polímeros. Diseñado para colocaciones en condiciones ambientales rigurosas.", 1),
(35, "weber basic ceramicas", "https://www.ar.weber/files/ar/styles/768x768_resize/public/pictures/2018-05/pack-weber-basicceramicas-30kg_2E1CDF7C1A274DFDBDD96E4AD87943C2.jpg", 1600, "Pegamento impermeable para colocar cerámicos en pisos y paredes interiores.", 1),
(36, "weber gris ceramicas", "https://www.ar.weber/files/ar/styles/768x768_resize/public/pictures/2018-05/pack-weber-grisceramicas-30kg_474B89BAE0974DDFA6BB2F6DBE78C24B.jpg", 1600, "Pegamento impermeable para colocar cerámicos en pisos y paredes interiores.", 1),
(37, "weber mix I", "https://www.ar.weber/files/ar/styles/768x768_resize/public/pictures/2018-05/pack-weber-mixI-30kg_366932105EBA4EFEAC13BDE1AF9E1FB6.jpg", 1600, "Mortero 2 en 1 para la realización de revoques interiores.", 1),
(38, "weber mix E", "https://www.ar.weber/files/ar/styles/768x768_resize/public/pictures/2018-05/pack-weber-mixE-30kg_22FF3B9FF7C94522AF6741B3B6809924.jpg", 1600, "Mortero 3 en 1 con ceresita de aplicación manual, para la realización de revoques exteriores.", 1),
